import { useState } from "react";
import Select from "react-dropdown-select";

const CourseFormScreen = () => {
  const [selectedCourses, setSelectedCourses] = useState([]);

  const options = [
    {
      id: "Computer Internet",
      courseCode: "CSE101",
      courseTitle: "Computer Internet",
      unit: 3,
    },
    { id: "MAT201", courseCode: "MAT201", courseTitle: "Statistics", unit: 4 },
  ];

  const handleCourseSelection = (values) => {
    setSelectedCourses(values);
  };

  return (
    <div className="course-form ">
      {/* Add Course button */}
      {/* <button onClick={handleAddCourse}>Add Course</button> */}

      {/* Course selection dropdown */}
      <Select
        options={options}
        onChange={(values) => handleCourseSelection(values)}
        labelField={"courseTitle"}
        valueField="id"
        searchBy="label"
        // multi
      />

      {/* Display selected courses in a table */}
      <table>
        <thead>
          <tr>
            <th>Course Code</th>
            <th>Course Title</th>
            <th>Unit</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {selectedCourses.map((course) => (
            <tr key={course.id}>
              <td>{course.courseCode}</td>
              <td>{course.courseTitle}</td>
              <td>{course.unit}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseFormScreen;
