import axios from "axios";

const BASE_URL = "http://localhost:5000/api/v1"; // Replace with your API base URL

const userApi = {
  register: async (formData) => {
    try {
      const response = await axios.post(
        `${BASE_URL}/students/register`,
        formData
      );
      return response.data; // Return the response data
    } catch (error) {
      throw new Error(error.response.data.message || "Registration failed");
    }
  },

  login: async (credentials) => {
    try {
      const response = await axios.post(
        `${BASE_URL}/students/login`,
        credentials
      );
      const token = response.data.token; // Get the token from the response
      console.log("token", token);
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`; // Set the token in the authorization header
      return response.data; // Return the response data
    } catch (error) {
      throw new Error(error.response.data.message || "Login failed");
    }
  },

  getProfile: async () => {
    try {
      const response = await axios.get(`${BASE_URL}/students/me`);
      return response.data; // Return the response data
    } catch (error) {
      throw new Error(
        error.response.data.message || "Error fetching student profile"
      );
    }
  },
};

export default userApi;
