import React, { useEffect, useState } from "react";
import useStore from "../../store";
import courseApi from "../../API/courseApi";
import "./style.scss";

const MyCourseScreen = () => {
  const userProfile = useStore((state) => state.userProfile);
  const [semester, setSemester] = useState("");
  const [semesters, setSemesters] = useState("");
  const [courseForms, setCourseForms] = useState([]);
  const userId = userProfile.userId;

  //    fetchSemesters();

  useEffect(() => {
    if (semester) {
      // Fetch course forms based on the selected semester
      fetchCourseFormsBySemester(semester);
    }
  }, [semester]);

  const fetchCourseFormsBySemester = async (selectedSemester) => {
    try {
      const response = await courseApi.getCoursesBySemester(
        userId,
        selectedSemester
      );
      setCourseForms(response);
    } catch (error) {
      console.log("Error fetching course forms:", error);
    }
  };

  const handleSemesterChange = (e) => {
    setSemester(e.target.value);
  };

  const handleViewCourseForm = (courseForm) => {
    // Handle displaying the selected course form
    console.log(courseForm);
  };

  return (
    <div>
      {/* Semester selection */}
      <select value={semester} onChange={handleSemesterChange}>
        <option value="">Select Semester</option>
        <option value="2021/2022">2022</option>
        <option value="Spring">Spring</option>
        <option value="Summer">Summer</option>
      </select>

      {/* Course forms */}
      <table>
        <thead>
          <tr>
            <th>Semester</th>
            <th>Section</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courseForms.map((courseForm) => (
            <tr key={courseForm._id}>
              <td>{courseForm.semester}</td>
              <td>{courseForm.section}</td>
              <td>
                <button onClick={() => handleViewCourseForm(courseForm)}>
                  View
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MyCourseScreen;
